import React, { Component } from 'react';
import Todos from './components/Todos'
import './App.css';
import Quote from './components/Quote';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      quote:{
        text:'',
        author:''
      }
    }
    this.refreshQuote = this.refreshQuote.bind(this);
  }


  refreshQuote(){
    fetch("https://cors-anywhere.herokuapp.com/http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1")
    .then(res=>res.json())
    .then((response)=>{
      JSON.stringify(response);
      //since I will be getting a list of one item.
      this.setState(
        {
          quote:{
            text:response[0].content,
            author:response[0].title
          }
        }
      )
    });
  }

  render() {
    return (
      <div id='quote-box' className="App">
        <h1>My App</h1>
        <Quote quote={this.state.quote}/>
        <button id='new-quote' onClick={this.refreshQuote}>Refresh</button>
        <a id='tweet-quote' href='twitter/intent/tweet'>Tweet This quote</a>
      </div>
    );
  }
}

export default App;
