import React, { Component } from 'react';
import Todos from './components/Todos'
import './App.css';
import Quote from './components/Quote';

class App extends Component {
  state = {
    quote:{
      text:'',
      author:''
    }
  }

  componentDidMount(){
    this.refreshQuote();
  }

  refreshQuote(){
    fetch("https://cors-anywhere.herokuapp.com/http://quotesondesign.com/wp-json/posts?filter[orderby]=rand&filter[posts_per_page]=1",
    {
      method:'get',
      headers:{
        "X-RapidAPI-Key":"5d69b031e4msh9b02b1a9bb4de6dp1c8a3ajsn5a222d91d805"
      }
    }).then(res=>res.json())
    .then((response)=>{
      JSON.stringify(response);
      //since I awill be getting a list of one item.
      this.setState(
        {
          quote:{
            text:response[0].content,
            author:response[0].title
          }
        }
      )
    });


    //console.log(quotes);
  }

  render() {
    return (
      <div id='quote-box' className="App">
        <h1>My App</h1>
        <Quote quote={this.state.quote}/>
      </div>
    );
  }
}

export default App;
